    	this.containsLyrics(staffs);
      var staffgroup = new abc_staff_group_element();
    	this.tempoSet = false;
      for (var s = 0; s < staffs.length; s++) {
    	  if (hint)
    		  this.restoreState();
    	  hint = false;
        this.createABCStaff(staffgroup, staffs[s], tempo, s);
      }
      return staffgroup;
